import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Navigate from "./Navigate";
import Col from "react-bootstrap/Col";
import { Row } from 'react-bootstrap';

export class Rating extends Component {
  constructor(props){
    super(props);
    this.state={
      email:null,
      ratings:[],
      musiclist:[],
      rec:false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentWillMount() {
    if(this.props === undefined || this.props === null){
      this.props.history.push('/unauthorized');
    }
    else{
      if(this.props.email === undefined || this.props.email === null){
       this.props.history.push('/unauthorized');
      }
     this.setState({email:this.props.email},console.log("r:Get email from app component "+this.props.email));
    }
  }

  componentDidMount(){
      console.log("get rating "+this.state.email);
    const url = 'http://localhost:8080/getUserRatings?email='+this.state.email;
    fetch(url, { 
            method: 'GET'
        })
        .then(
            (response)=>{ return response.json() }
          )
        .then(data=>{
            if(data!==null){
              this.setState({
                ratings: data.map(item => ({
                    song_name: item.song_name,
                    song_artist: item.song_artist,
                    rate:item.rate,
                    comment:item.comment
                })
                ),rec:true})
            }
            else{
                this.setState({rec:false});
            }
          }
          );
  }

  handleClick(){
    const url = 'http://localhost:8080//getRecommendByUserName?email='+this.state.email;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .then(data=>{
            if(data!==null){
                this.setState({
                    musiclist: data.map(item => ({
                        song_name: item.song_name,
                        song_artist: item.song_artist,
                        strength:item.strength
                    })
                    )})
        }}
        );
  }
  render() {
    return (
        <Container fluid>
        <Navigate/>
        <Container fluid="lg">
        <Container fluid="lg">
        <Col md={4}>
        <Form>
          <Row>
          <Form.Group controlId="recommendation">
            <Form.Label style={{fontSize: '27px',fontWeight: 'bold'}}>Recommend music based on other users' and your ratings</Form.Label>
          </Form.Group>
          <Button variant="dark" onClick={this.handleClick}>Get recommendation</Button>
          </Row>
        </Form>
        </Col><br/><br/><br/>
        <Container fluid="lg">
        {this.state.rec ? (this.state.musiclist.map(( listValue, index ) => {
          return (
            <Row key={index}>
              <Col key={index} >Num {index+1} suggested music: </Col>
              <Col >
               Song name : {listValue.song_name}
               </Col >
               <Col >
               Song artist : {listValue.song_artist}
               </Col>
               <Col >
               Recommended strength : {listValue.strength}
               </Col ><br/><br/>
            </Row>
          );
        })):(()=>{
            return(
                <Row >
                    Sry! No recommendation for you 
                </Row>
            )
        })}
        </Container>
      </Container>
          <Container fluid="lg"><br/><br/>
            {this.state.ratings.map(( listValue, index ) => {
          return (
            <Row key={index}>
               <Col style={{ color: 'red' }}>
               Song name : {listValue.song_name}
               </Col >
               <Col style={{ color: 'red' }}>
               Song artist : {listValue.song_artist}
               </Col>
               <Col style={{ color: 'blue' }}>
               Your rating : {listValue.rate}
               </Col>
               <Col style={{ color: 'blue' }}>
               Your comment : {listValue.comment}
               </Col><br/>
            </Row>
          );
        })}
          </Container>
        </Container>
        </Container>
      );
  }
}

  export default withRouter(Rating);
