import React, { Component } from 'react';
import { withRouter,Link } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Navigate from "./Navigate";


export class Playlist extends Component {

  constructor(props){
    super(props);
    this.state={
      email:null,
      mood: null,
      playlist: [],
      validated:false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(event){
    const form = event.currentTarget;
      if (form.checkValidity() === false) {
        event.preventDefault();
        event.stopPropagation();
      }
  
      this.setState({validated:true})
    const url = 'http://localhost:8080/getPlaylist?key='+this.state.mood;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .then(data=>{this.setState({
        playlist: data.map(item => ({
            title: item.playlist_name,
            url: item.playlist_url
        })
        )})}
        );
        
  }
  componentWillMount() {
    if(this.props === undefined || this.props === null){
      this.props.history.push('/unauthorized');
    }
    else{
      if(this.props.email === undefined || this.props.email === null){
       this.props.history.push('/unauthorized');
      }
     this.setState({email:this.props.email});
    }
  }

  componentDidMount(){
    console.log("playlist email "+ this.state.email);
  }

  render() {
    return (
        <Container fluid>
        <Navigate/>
        <Container fluid="lg">
          <Container fluid="lg">
          <Row>
          <Col md={5}>
            <Form validated={this.state.validated}>
              <Form.Group controlId="exampleForm.ControlTextarea1">
                <Form.Label>What's your mood today?</Form.Label>
                <Form.Control as="textarea" required rows="1" onChange={e=>this.setState({mood:e.target.value})}/>
              </Form.Group>
              <Button variant="success" type="submit">Let's find you your playlist..</Button>
            </Form>
          </Col>
          </Row><br/>
          {this.state.playlist.map(( listValue, index ) => {
          return (
            <Row key={index}>
              <Col key={index} md={5}>Num {index+1} suggested {listValue.title} playlist: </Col>
              <Col key={listValue.url} md={5}>
              <li><a href={listValue.url} target="_blank">{listValue.url}</a></li>
              </Col>
            </Row>
          );
        })}
          </Container>
        </Container>
        </Container>
      );
  } 
}
  export default withRouter(Playlist);
