import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Navigate from "./Navigate";
import Col from "react-bootstrap/Col";
import { Row } from 'react-bootstrap';

export class Rating extends Component {
  constructor(props){
    super(props);
    this.state={
      email:null,
      ratings:[]
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentWillMount() {
    if(this.props === undefined || this.props === null){
      this.props.history.push('/unauthorized');
    }
    else{
      if(this.props.email === undefined || this.props.email === null){
       this.props.history.push('/unauthorized');
      }
     this.setState({email:this.props.email},console.log("r:Get email from app component "+this.props.email));
    }
  }

  handleClick(){
  }
  render() {
    return (
        <Container fluid>
        <Navigate/>
        <Container fluid="lg">
          <Container fluid="lg">
            {this.state.ratings.map(( listValue, index ) => {
          return (
            <Row key={index}>
               <Col>
               Song name : {listValue.song_name}
               </Col>
               <Col>
               Song artist : {listValue.song_artist}
               </Col>
               <Col>
               Your rating : {listValue.rate}
               </Col>
               <Col>
               Your comment : {listValue.comment}
               </Col>
            </Row>
          );
        })}
          </Container>
        </Container>
        </Container>
      );
  }
}

  export default withRouter(Rating);
