import React, { Component } from "react";
import { withRouter} from "react-router-dom";
import Container from "react-bootstrap/Container";
import Navigate from "./Navigate";
import Billboard from "./Billboard";

export class Home extends Component {

  constructor(props){
    super(props);
    this.state = {
      songList:[],
      error:"no",
      email:null
    };
    this.handleRating = this.handleRating.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }
  componentWillMount() {
       if(this.props === undefined || this.props === null){
         this.props.history.push('/unauthorized');
       }
       else{
         if(this.props.email === undefined || this.props.email === null){
          this.props.history.push('/unauthorized');
         }
        this.setState({email:this.props.email},console.log("Get email from app component "+this.props.email));
        fetch('http://localhost:5080/billboard100', { 
            method: 'POST'
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .catch(error=>{this.setState({error:error})})
        .then(data=>this.setState({songList:data}));
       }
  }
  componentDidMount() {
  }

  handleRating(name,artist){
    const url = 'http://localhost:8080/getRating2?email='+this.state.email+"&song_name="+name+"&song_artist="+artist;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .then(data=>{
          if(data===-1){
            alert(" You haven't rated it ");
          }
          else{
            alert(" Your rating is : "+data);
          }
        }
        );
  }

  handleEdit(rating,comment,name,artist){
    const url = 'http://localhost:8080/newRating?email='+this.state.email+"&song_name="+name+"&song_artist="+artist+"&rate="
    +rating+"&comment="+comment;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        );
  }
  
  render() {
    
        return (
          <Container fluid>
          <Navigate/>
          <Container fluid="lg">
            <Container fluid="lg">
            <Billboard handleRating={this.handleRating} handleEdit={this.handleEdit} songs={this.state.songList}/>
            </Container>
          </Container>
          </Container>
        );
  }

}

export default withRouter(Home);
