import React, { Component } from "react";
import { withRouter} from "react-router-dom";
import Container from "react-bootstrap/Container";
import Navigate from "./Navigate";
import Billboard from "./Billboard";
import _ from 'lodash';
import { Form,Col,Button} from 'react-bootstrap';
export class Home extends Component {

  constructor(props){
    super(props);
    this.state = {
      songList:[],
      error:"no",
      email:null,
      id:10,
      rating:5,
      comment:"aaaaaaaaaaaaaaaaaaaaaaaaaaaaa",
      song_name:"",
      song_artist:""
    };
    this.handleRating = this.handleRating.bind(this);
    this.handleEdit = this.handleEdit.bind(this);
  }
  componentWillMount() {
       if(this.props === undefined || this.props === null){
         this.props.history.push('/unauthorized');
       }
       else{
         if(this.props.email === undefined || this.props.email === null){
          this.props.history.push('/unauthorized');
         }
        this.setState({email:this.props.email},console.log("Get email from app component "+this.props.email));
        fetch('http://localhost:5080/billboard100', { 
            method: 'POST'
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .catch(error=>{this.setState({error:error})})
        .then(data=>this.setState({songList:data}));
       }
  }
  componentDidMount() {
  }

  handleRating(name,artist){
    const url = 'http://localhost:8080/getRating2?email='+this.state.email+"&song_name="+name+"&song_artist="+artist;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .then(data=>{
          if(data===-1){
            alert(" You haven't rated it ");
          }
          else{
            alert(" Your rating is : "+data);
          }
        }
        );
  }

  handleEdit(){
    this.state.songList.forEach((e)=>{
      if(e[0]===this.state.id){
        this.state.song_name=e[1];
        this.state.song_artist=e[2];
      }
    });
    const url = 'http://localhost:8080/newRating?email='+this.state.email+"&song_name="+this.state.song_name+"&song_artist="+this.song_artist+"&rate="
    +this.state.rating+"&comment="+this.state.comment;
    fetch(url, { 
            method: 'POST' 
        }) 
        .then(
          (response)=>{ return response.json() }
        );
  }
  
  render() {
    
        return (
          <Container fluid>
          <Navigate/>
          <Container fluid="lg">
          <Container>
            <Form>
            <Col>
            <Form.Group as={Col} controlId="idid">
            <Form.Control as="select" required onChange={e=>this.setState({id:e.target.value})}>
            { _.range(1, 101).map(value => <option key={value} value={value}>{value}</option>) }
            </Form.Control>
            </Form.Group>
            </Col>
            <Col>
            <Form.Group as={Col} controlId="rating">
            <Form.Control as="select" required onChange={e=>this.setState({rating:e.target.value})}>
            <option>1</option>
            <option>2</option>
            <option>3</option>
            <option>4</option>
            <option>5</option>
            </Form.Control>
            </Form.Group>
            </Col>
            <Col>
            <Form.Group controlId="comment">
            <Form.Control as="textarea"  placeholder="Good music" rows="2" onChange={e=>this.setState({comment:e.target.value})}/>
            </Form.Group>
            </Col>
            <Col>
            <Button variant="info" onClick={this.handleEdit()} style={{margin:'10px'}}>Submit</Button>
            </Col>
            </Form>
            </Container>
            <Container fluid="lg">
            <Billboard handleRating={this.handleRating} handleEdit={this.handleEdit} songs={this.state.songList}/>
            </Container>
          </Container>
          </Container>
        );
  }

}

export default withRouter(Home);
