import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Navigate from "./Navigate";

export class Playlist extends Component {

  constructor(props){
    super(props);
    this.state={
      mood: null,
      playlist: [1,2,3,4,5],
      playlistCount: 5
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
    this.setState({playlist:[1,2,3,4,5,6]});
    /*
    fetch('http://localhost:8080/fetchList', { 
            method: 'POST', 
            headers: { 
              "Access-Control-Allow-Origin": "*",
              "Access-Control-Allow-Methods": "POST",
              "Access-Control-Allow-Headers": "Authorization",
              "Content-Type": "application/json" 
            }, 
            body: JSON.stringify({mood: this.state.mood}) 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .catch(error=>console.log(error))
        .then(data=>this.setState({
          playlist: data['playlist'],
        }));
        this.setState({playlistCount:this.state.playlist.size()});
        */
  }

  render() {
    return (
        <Container fluid>
        <Navigate/>
        <Container fluid="lg">
          <Container fluid="lg">
          <Row>
          <Col md={5}>
            <Form>
              <Form.Group controlId="exampleForm.ControlTextarea1">
                <Form.Label>What's your mood today?</Form.Label>
                <Form.Control as="textarea" rows="1" onChange={e=>this.setState({mood:e.target.value})}/>
                {console.log(this.state.mood)}
              </Form.Group>
              <Button variant="success" onClick={this.handleClick}>Let's find you your playlist..</Button>
            </Form>
          </Col>
          </Row><br/>
          {this.state.playlist.map(( listValue, index ) => {
          return (
            <Row><Col md={5}>Current suggested playlists: {listValue}</Col></Row>
          );
        })}
          </Container>
        </Container>
        </Container>
      );
  } 
}
  export default withRouter(Playlist);
