import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Navigate from "./Navigate";


export class Playlist extends Component {

  constructor(props){
    super(props);
    this.state={
      email:null,
      mood: null,
      playlist: [],
      musiclist:[]
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleClick2 = this.handleClick2.bind(this);
  }

  handleClick(){
  
    const url = 'http://localhost:8080/getPlaylist?key='+this.state.mood;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .then(data=>{this.setState({
        playlist: data.map(item => ({
            title: item.playlist_name,
            url: item.playlist_url
        })
        )})}
        );
        
  }
  handleClick2(){
  
    const url = 'http://localhost:8080//getRecommendByUserName?email='+this.state.email;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .then(data=>{this.setState({
        playlist: data.map(item => ({
            song_name: item.song_name,
            song_artist: item.song_artist,
            strength:item.strength
        })
        )})}
        );
        
  }
  componentWillMount() {
    if(this.props === undefined || this.props === null){
      this.props.history.push('/unauthorized');
    }
    else{
      if(this.props.email === undefined || this.props.email === null){
       this.props.history.push('/unauthorized');
      }
     this.setState({email:this.props.email});
    }
  }

  componentDidMount(){
    console.log("playlist email "+ this.state.email);
  }

  render() {
    return (
        <Container fluid>
        <Navigate/>
        <Container fluid="lg">
          <Container fluid="lg">
          <Row>
          <Col md={5}>
            <Form validated>
              <Form.Group controlId="exampleForm.ControlTextarea1">
                <Form.Label>What's your mood today?</Form.Label>
                <Form.Control as="textarea" required rows="1" onChange={e=>this.setState({mood:e.target.value})}/>
              </Form.Group>
              <Button variant="success" onClick={this.handleClick}>Let's find you your playlist..</Button>
            </Form>
          </Col>
          </Row><br/>
          {this.state.playlist.map(( listValue, index ) => {
          return (
            <Row key={index}>
              <Col key={index} md={5}>Num {index+1} suggested {listValue.title} playlist: </Col>
              <Col key={listValue.url} md={5}>
              <li><a href={listValue.url} target="_blank">{listValue.url}</a></li>
              </Col>
            </Row>
          );
        })}
          </Container>
          <Container fluid="lg">
        <Col md={4}>
        <Form>
          <Form.Group controlId="recommendation">
            <Form.Label>Recommend music based on other users' and your ratings</Form.Label>
          </Form.Group>
          <Button variant="dark" onClick={this.handleClick2}>Get recommendation</Button>
        </Form>
        </Col><br/><br/><br/>
        <Row>
        {this.state.musiclist.map(( listValue, index ) => {
          return (
            <Row key={index}>
              <Col key={index} md={5}>Num {index+1} suggested {listValue.title} music: </Col>
              <Col key={listValue.music} md={5}>
              <p>{listValue.song_name}</p>
              <p>{listValue.song_artist}</p>
              <p>{listValue.strength}</p>
              </Col>
            </Row>
          );
        })}
        </Row>
      </Container>
        </Container>
        </Container>
      );
  } 
}
  export default withRouter(Playlist);
