import React, { Component } from 'react';
import { withRouter } from "react-router-dom";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import Col from "react-bootstrap/Col";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Navigate from "./Navigate";

export class Playlist extends Component {

  constructor(props){
    super(props);
    this.state={
      mood: null,
      playlist: [],
      playlistCount: 5
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(){
    //this.setState({playlist:[1,2,3,4,5,6]});
    
    const url = 'http://localhost:8080/getPlaylist?key='+this.state.mood;
    fetch(url, { 
            method: 'GET' 
        }) 
        .then(
          (response)=>{ return response.json() }
        )
        .then(data=>{this.setState({
        playlist: data.map(item => ({
            title: item.playlist_name,
            url: item.playlist_url
        })
        )})}
        );
        
  }

  render() {
    return (
        <Container fluid>
        <Navigate/>
        <Container fluid="lg">
          <Container fluid="lg">
          <Row>
          <Col md={5}>
            <Form>
              <Form.Group controlId="exampleForm.ControlTextarea1">
                <Form.Label>What's your mood today?</Form.Label>
                <Form.Control as="textarea" rows="1" onChange={e=>this.setState({mood:e.target.value})}/>
                {console.log(this.state.mood)}
              </Form.Group>
              <Button variant="success" onClick={this.handleClick}>Let's find you your playlist..</Button>
            </Form>
          </Col>
          </Row><br/>
          {this.state.playlist.map(( listValue, index ) => {
          return (
            <Row key={index}><Col key={index} md={5}>Current suggested playlists: {listValue.url}</Col></Row>
          );
        })}
          </Container>
        </Container>
        </Container>
      );
  } 
}
  export default withRouter(Playlist);
